// Admin components
export const AdminDashboard: React.FC = () => (
  <div>
    <h1 className="text-3xl font-bold text-gray-900 mb-6">Admin Dashboard</h1>
    {/* Admin dashboard would be implemented here */}
    <div className="card">
      <p className="text-gray-600">Admin dashboard component to be implemented</p>
    </div>
