);

export const DatasetUpload: React.FC = () => (
  <div>
    <h1 className="text-3xl font-bold text-gray-900 mb-6">Dataset Upload</h1>
    {/* Dataset upload would be implemented here */}
    <div className="card">
      <p className="text-gray-600">Dataset upload component to be implemented</p>
